#include <iostream>\n\nint main() {\n    int n, num, digit, rev = 0;\n    std::cout << "Enter a positive number: ";\n    std::cin >> num;\n\n    n = num;\n\n    while (num != 0) {\n        digit = num % 10;\n        rev = (rev * 10) + digit;\n        num = num / 10;\n    }\n\n    if (n == rev)\n        std::cout << n << " is a palindrome." << std::endl;\n    else\n        std::cout << n << " is not a palindrome." << std::endl;\n\n    return 0;\n}